  std::string htoascii(const std::string& in) {
    int len = in.length();
    std::string newString;
    for(int i=0; i< len; i+=2){
    std::string sstr = in.substr(i,2);
    char chr = (char) (int)strtol(sstr.c_str(), 0x00, 16);
    newString.push_back(chr);
}
   return newString;
}
